INSERT INTO `statti` (`id`, `title`, `text`, `date`, `img_src`) VALUES
(1, 'Вариант 1', '<pre><code class=\"language-html\">\r\n&lt;html&gt;\r\n	&lt;head&gt;\r\n		&lt;meta charset=&quot;utf-8&quot;&gt;\r\n		&lt;title&gt;1&lt;/title&gt;\r\n\r\n		&lt;style type=&quot;text/css&quot;&gt;\r\n	   		TABLE {\r\n    			border-collapse: collapse;\r\n   			}\r\n   			TD, TH {\r\n    			padding: 10px;\r\n    			border: 2px solid #00BFFF ;\r\n    		}\r\n\r\n    button.new {\r\n	background: -moz-linear-gradient(#00BBD6, #EBFFFF);\r\n	background: -webkit-gradient(linear, 0 0, 0 100%, from(#00BBD6), to(#EBFFFF));\r\n	filter: progid:DXImageTransform.Microsoft.gradient(startColorstr=\'#00BBD6\', endColorstr=\'#EBFFFF\');\r\n	padding: 3px 7px;\r\n	color: #333;\r\n	-moz-border-radius: 5px;\r\n	-webkit-border-radius: 5px;\r\n	border-radius: 5px;\r\n	border: 1px solid #666;\r\n\r\n	 \r\n\r\n   			\r\n  		&lt;/style&gt;\r\n		\r\n	&lt;/head&gt;\r\n	&lt;body&gt;\r\n		&lt;h1&gt;Покупка авиабилетов&lt;/h1&gt;\r\n		&lt;form name=&quot;Shop&quot; method=&quot;post&quot;&gt;\r\n	&lt;table&gt;\r\n    	&lt;tr&gt;\r\n        	&lt;td colspan=&quot;2&quot;&gt; \r\n        		&lt;b&gt;Откуда:&lt;/b&gt;&lt;input type=text  &gt; \r\n        		&lt;b&gt;Куда:&lt;/b&gt;&lt;input type=text &gt;\r\n        	&lt;/td&gt;\r\n        	&lt;td align=&quot;center&quot;&gt;\r\n        		&lt;b&gt;Количество пересадок :  &lt;/b&gt;&lt;input type=number min=&quot;0&quot;  max =&quot;2&quot; value=&quot;0&quot; &gt;\r\n        	&lt;/td&gt;\r\n        	&lt;td rowspan=&quot;4&quot; align=&quot;right&quot;&gt;\r\n        		&lt;b&gt;Взрослые: &lt;/b&gt;&lt;input type=number min=&quot;0&quot; value=&quot;0&quot;&gt; &lt;br&gt;&lt;br&gt;\r\n        		&lt;b&gt;Дети: &lt;/b&gt;&lt;input type=number min=&quot;0&quot; value=&quot;0&quot;&gt; &lt;br&gt;&lt;br&gt;\r\n        		&lt;b&gt;Младенцы: &lt;/b&gt;&lt;input type=number min=&quot;0&quot; value=&quot;0&quot;&gt; &lt;br&gt;&lt;br&gt;\r\n        		&lt;div align=&quot;center&quot;&gt;\r\n        		&lt;b&gt;Класс:&lt;/b&gt; \r\n						&lt;SELECT&gt;\r\n	    					&lt;OPTION value=&quot;Любой&quot;&gt;Любой\r\n	    					&lt;OPTION value=&quot;Эконом&quot;&gt;Эконом\r\n	    					&lt;OPTION value=&quot;Комфорт&quot;&gt;Комфорт\r\n						    &lt;OPTION value=&quot;Бизнес&quot;&gt;Бизнес\r\n						    &lt;OPTION value=&quot;Первый класс&quot;&gt;Первый класс\r\n						&lt;/select&gt;\r\n				&lt;/div&gt;\r\n        	 &lt;/td&gt;\r\n    	&lt;/tr&gt;\r\n\r\n   		 &lt;tr&gt;\r\n        	&lt;td colspan=&quot;2&quot;&gt;\r\n        		&lt;b&gt;Дата: &lt;/b&gt;&lt;input type=&quot;date&quot;&gt; \r\n        		 &lt;b&gt;Дата обратно: &lt;/b&gt;&lt;input type=&quot;date&quot;&gt;\r\n        	&lt;/td&gt;\r\n        	&lt;td align=&quot;center&quot; &gt;&lt;B&gt;Цена билета до (в тыс. руб): &lt;/B&gt; &lt;br&gt;\r\n        		&lt;b&gt;0&lt;/b&gt;\r\n        		&lt;input type=&quot;range&quot;\r\n					min=&quot;0&quot;\r\n					max=&quot;100&quot;\r\n					step=&quot;1&quot;\r\n					value=&quot;50&quot;&gt;\r\n        	&lt;B&gt;100&lt;/B&gt;\r\n\r\n			&lt;/td&gt;\r\n        	\r\n   		 &lt;/tr&gt;\r\n\r\n    	&lt;tr&gt;\r\n        	&lt;td align=&quot;center&quot;&gt;&lt;B&gt;Багаж: &lt;/B&gt;&lt;input type=checkbox &gt;&lt;/td&gt;\r\n       		&lt;td align=&quot;center&quot;&gt;&lt;B&gt;Страховка: &lt;/B&gt;&lt;input type=checkbox &gt;&lt;/td&gt;\r\n        	&lt;td&gt;\r\n        &lt;b&gt;Авиакомпания:&lt;/b&gt; \r\n				&lt;SELECT &gt;\r\n	    			&lt;OPTION value=Любая&gt;Любая\r\n	    			&lt;OPTION value=&quot;S7 Airlines&quot;&gt;S7 Airlines\r\n	    			&lt;OPTION value=Аэрофлот&gt;Аэрофлот\r\n				    &lt;OPTION value=&quot;Уральские авиалинии&quot;&gt;Уральские авиалинии\r\n				    &lt;OPTION value=Победа&gt;Победа\r\n				    &lt;OPTION value=&quot;Israir Airlines&quot;&gt;Israir Airlines\r\n				    &lt;OPTION value=&quot;Ред Вингс&quot;&gt;Ред Вингс\r\n				    &lt;OPTION value=&quot;Fly One&quot;&gt;Fly One\r\n				&lt;/select&gt;\r\n        	&lt;/td&gt;\r\n        	\r\n    	&lt;/tr&gt;\r\n\r\n\r\n    	&lt;tr&gt;\r\n        	&lt;td colspan=&quot;3&quot; align=&quot;center&quot;&gt;\r\n        		&lt;button  class=&quot;new&quot;&gt;Найти билеты --&gt; &lt;/button&gt;\r\n        	&lt;/td&gt;\r\n        	\r\n   		 &lt;/tr&gt;\r\n\r\n   		 &lt;tr align=&quot;center&quot;&gt; \r\n\r\n	&lt;td colspan=&quot;4&quot;&gt; &lt;textarea name=prm9 rows=5 cols= 140 \r\n        placeholder=&quot;Отзыв о серисе&quot;&gt;&lt;/textarea&gt; &lt;/td&gt; \r\n				&lt;/tr&gt;\r\n	&lt;/table&gt;\r\n		&lt;/form&gt;\r\n	&lt;/body&gt;\r\n&lt;/html&gt;\r\n</code></pre>', '2020-04-05', 'file/v1.jpg'),
(2, 'Вариант 2', '<pre><code class=\"language-html\">&lt;html&gt;\r\n	&lt;head&gt;\r\n		&lt;meta charset=&quot;utf-8&quot;&gt;\r\n		&lt;title&gt;2&lt;/title&gt;\r\n\r\n		&lt;style type=&quot;text/css&quot;&gt;\r\n	   		TABLE {\r\n    			border-collapse: collapse;\r\n\r\n   			}\r\n   			TD, TH {\r\n    			padding: 12px;\r\n    			border: 2px solid black ;\r\n\r\n   			\r\n  		&lt;/style&gt;\r\n		\r\n	&lt;/head&gt;\r\n	&lt;body&gt;\r\n		&lt;h1&gt;Бронирование жилья &lt;/h1&gt;\r\n		&lt;form name=&quot;Shop&quot; method=&quot;post&quot;&gt;\r\n		&lt;table &gt;\r\n			    &lt;tr&gt;\r\n			&lt;td colspan=&quot;2&quot;&gt;&lt;b&gt;Место/название объекта: &lt;/b&gt;&lt;input type=text  &gt;  &lt;/td&gt;\r\n			&lt;td align=&quot;center&quot;&gt;  &lt;b&gt;Количество звезд: &lt;/b&gt; &lt;br&gt;\r\n			 &lt;input name=&quot;zveda&quot; type=&quot;radio&quot; value=&quot;любое&quot; checked&gt; любое \r\n			 &lt;input name=&quot;zveda&quot; type=&quot;radio&quot; value=&quot;без звёзд&quot; &gt; без звёзд &lt;br&gt;\r\n			 &lt;input name=&quot;zveda&quot; type=&quot;radio&quot; value=&quot;1&quot;&gt; 1\r\n	&lt;input name=&quot;zveda&quot; type=&quot;radio&quot; value=&quot;2&quot;&gt; 2\r\n	&lt;input name=&quot;zveda&quot; type=&quot;radio&quot; value=&quot;3&quot;&gt; 3\r\n &lt;input name=&quot;zveda&quot; type=&quot;radio&quot; value=&quot;4&quot;&gt; 4\r\n	&lt;input name=&quot;zveda&quot; type=&quot;radio&quot; value=&quot;5&quot;&gt; 5\r\n					    \r\n\r\n\r\n			        &lt;/td&gt;\r\n			        &lt;td rowspan=&quot;5&quot; &gt;\r\n			            &lt;H4&gt;Удобства в номере: &lt;/h4&gt;\r\n			        	Кухня/мини-кухня  &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n			        	Собственная ванная комната  &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n			        	Кондиционер  &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n			        	Ванна &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n			        	Балкон &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n			        	Звукоизоляция &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n			        	Стиральная машина &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n\r\n			        &lt;/td&gt;\r\n			    &lt;/tr&gt;\r\n			    &lt;tr&gt;\r\n			        &lt;td colspan=&quot;2&quot; &gt;&lt;b&gt;Дата заезда: &lt;/b&gt;&lt;input type=&quot;date&quot;&gt; &lt;/td&gt;\r\n			        &lt;td&gt;&lt;b&gt;Тип размещения: &lt;/b&gt; \r\n			        	&lt;SELECT&gt;\r\n	    					&lt;OPTION value=&quot;Любой&quot;&gt;Любой\r\n	    					&lt;OPTION value=&quot;Квартира&quot;&gt;Квартира\r\n	    					&lt;OPTION value=&quot;Гостевые дома&quot;&gt;Гостевые дома\r\n						    &lt;OPTION value=&quot;Отели&quot;&gt;Отели\r\n						    &lt;OPTION value=&quot;Хостелы&quot;&gt;Хостелы\r\n						&lt;/select&gt;\r\n			        &lt;/td&gt;\r\n			    &lt;/tr&gt;\r\n			    &lt;tr&gt;\r\n			        &lt;td colspan=&quot;2&quot;&gt;&lt;b&gt;Дата отъезда: &lt;/b&gt;&lt;input type=&quot;date&quot;&gt;&lt;/td&gt;\r\n			        &lt;td&gt;\r\n			        	&lt;b&gt;Оценки по отзывам: &lt;/b&gt; \r\n			        	&lt;SELECT&gt;\r\n	    					&lt;OPTION value=&quot;Любые&quot;&gt;Любые\r\n	    					&lt;OPTION value=&quot;9+&quot;&gt;9+\r\n	    					&lt;OPTION value=&quot;8+&quot;&gt;8+\r\n						    &lt;OPTION value=&quot;7+&quot;&gt;7+\r\n						    &lt;OPTION value=&quot;6+&quot;&gt;6+\r\n						&lt;/select&gt;\r\n			        &lt;/td&gt;\r\n			    &lt;/tr&gt;\r\n			    &lt;tr&gt;\r\n			        &lt;td colspan=&quot;2&quot;&gt;&lt;b&gt;Количество взрослых : \r\n			        &lt;/b&gt;&lt;input type=number min=&quot;0&quot; value=&quot;0&quot;&gt;&lt;/td&gt;\r\n			        &lt;td align=&quot;center&quot;&gt;&lt;b&gt;Цена до (в тыс. руб): &lt;/b&gt; &lt;br&gt;\r\n			        	&lt;b&gt;0&lt;/b&gt;\r\n        		&lt;input type=&quot;range&quot;\r\n					min=&quot;0&quot;\r\n					max=&quot;100&quot;\r\n					step=&quot;1&quot;\r\n					value=&quot;50&quot;&gt;\r\n        	&lt;B&gt;100&lt;/B&gt;\r\n			        &lt;/td&gt;\r\n			    &lt;/tr&gt;\r\n			    &lt;tr&gt;\r\n			        &lt;td&gt;&lt;b&gt;Количество детей: &lt;/b&gt; &lt;br&gt;&lt;input \r\n			        	type=number min=&quot;0&quot; value=&quot;0&quot;&gt;&lt;/td&gt;\r\n			        &lt;td&gt;&lt;b&gt;Количетсво номеров: &lt;/b&gt;&lt;br&gt;&lt;input \r\n			        	type=number min=&quot;0&quot; value=&quot;0&quot;&gt;&lt;/td&gt;\r\n			        &lt;td&gt;&lt;b&gt;Подобрать самолет на эти даты: &lt;/b&gt;&lt;input type=checkbox &gt;\r\n			        &lt;/td&gt;\r\n			    &lt;/tr&gt;\r\n\r\n			    &lt;tr align=&quot;center&quot;&gt; \r\n					&lt;td colspan=&quot;4&quot;&gt; &lt;textarea name=prm9\r\n						rows=6 cols= 118 placeholder=&quot;Отзыв о серисе&quot;&gt;&lt;/textarea&gt; &lt;/td&gt; \r\n				&lt;/tr&gt;\r\n\r\n			    &lt;tr&gt;\r\n        	&lt;td colspan=&quot;4&quot; align=&quot;center&quot;&gt;\r\n        		&lt;button  class=&quot;new&quot;&gt;Найти жилье --&gt; &lt;/button&gt;\r\n        	&lt;/td&gt;\r\n        	\r\n   		 &lt;/tr&gt;\r\n	&lt;/table&gt;\r\n		&lt;/form&gt;\r\n	&lt;/body&gt;\r\n&lt;/html&gt;</code></pre>', '2020-04-19', 'file/v2.jpg'),
(3, 'Вариант 3', '<pre><code class=\"language-html\">&lt;html&gt;\r\n	&lt;head&gt;\r\n		&lt;meta charset=&quot;utf-8&quot;&gt;\r\n		&lt;title&gt;3&lt;/title&gt;\r\n\r\n		&lt;style type=&quot;text/css&quot;&gt;\r\n	   		TABLE {\r\n    			border-collapse: collapse;\r\n\r\n   			}\r\n   			TD, TH {\r\n    			padding: 12px;\r\n    			border: 1px solid black ;\r\n\r\n   			\r\n  		&lt;/style&gt;\r\n		\r\n	&lt;/head&gt;\r\n	&lt;body&gt;\r\n		&lt;h1&gt;Carsharing&lt;/h1&gt;\r\n		&lt;form name=&quot;Shop&quot; method=&quot;post&quot;&gt;\r\n		&lt;table&gt;\r\n    &lt;tr&gt;\r\n        &lt;td rowspan=&quot;3&quot; align=&quot;left&quot;&gt;\r\n        	&lt;b&gt;ФИО: &lt;/b&gt;&lt;input type=text size=&quot;33&quot;&gt; &lt;br&gt;\r\n        	&lt;b&gt;Дата рождения: &lt;/b&gt;&lt;input type=date size=&quot;23&quot;&gt; &lt;br&gt; \r\n        	&lt;b&gt;Место рождения: &lt;/b&gt;&lt;input type=text size=&quot;22&quot;&gt; &lt;br&gt;\r\n        	&lt;b&gt;Серия и номер: &lt;/b&gt;&lt;input type=text size=&quot;24&quot;&gt; &lt;br&gt;\r\n        	&lt;b&gt;Кем выдан: &lt;/b&gt;&lt;input type=text size=&quot;28&quot;&gt; &lt;br&gt;\r\n        	&lt;b&gt;Дата выдачи: &lt;/b&gt;&lt;input type=date size=&quot;26&quot;&gt; &lt;br&gt; \r\n        	&lt;b&gt;Код подразделения: &lt;/b&gt;&lt;input type=text &gt; &lt;br&gt;\r\n        	&lt;b&gt;&lt;div align=&quot;left&quot;&gt;Номер водительского&lt;/div&gt; \r\n        	удостоверения: &lt;/b&gt;&lt;input type=text size=&quot;24&quot;&gt; &lt;br&gt;\r\n\r\n        &lt;/td&gt;\r\n        &lt;td&gt;\r\n        	&lt;b&gt;Автомобиль: &lt;/b&gt; \r\n			        	&lt;SELECT&gt;\r\n	    					&lt;OPTION value=&quot;Все авто&quot;&gt;Все авто\r\n	    					&lt;OPTION value=&quot;Hyundai Solaris&quot;&gt;Hyundai Solaris\r\n	    					&lt;OPTION value=&quot;Renault Kaptur &quot;&gt;Renault Kaptur \r\n						    &lt;OPTION value=&quot;Volkswagen Polo&quot;&gt;Volkswagen Polo\r\n						    &lt;OPTION value=&quot;BMW 320i&quot;&gt;BMW 320i\r\n						&lt;/select&gt;\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td&gt;\r\n        	&lt;b&gt;Тарифы: &lt;/b&gt; \r\n			        	&lt;SELECT&gt;\r\n	    					&lt;OPTION value=&quot;4 - 12 руб/мин&quot;&gt;4-12 руб/мин\r\n	    					&lt;OPTION value=&quot;На сутки&quot;&gt;На сутки\r\n	    					\r\n						&lt;/select&gt;\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td&gt;&lt;b&gt;Адрес вашего местоположения &lt;br&gt;\r\n        	или желаемый адрес&lt;br&gt;( мы подберем ближайшую машину) : &lt;/b&gt;&lt;br&gt;\r\n        	&lt;input name=&quot;adres&quot; type=&quot;radio&quot; \r\n        	value=&quot;Мое местоположение&quot; checked&gt; мое местоположение &lt;Br&gt;\r\n        	&lt;input name=&quot;adres&quot; type=&quot;radio&quot; &gt; \r\n        	&lt;input  type=&quot;text&quot; size=&quot;34&quot; &gt;\r\n\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td&gt;&lt;b&gt;Прикрепить мое фото: &lt;/b&gt;&lt;br&gt;\r\n        	&lt;input type=file &gt;\r\n        &lt;/td&gt;\r\n        &lt;td&gt;\r\n        	&lt;b&gt;Номер бонусной карты: &lt;/b&gt;&lt;br&gt;&lt;input type=text size=&quot;23&quot;&gt; &lt;br&gt;\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td align=&quot;center&quot;&gt;&lt;button  class=&quot;new&quot;&gt;Найти машину --&gt; &lt;/button&gt;&lt;/td&gt;\r\n        &lt;td&gt;\r\n        	 &lt;textarea name=prm9 rows=3 cols= 36 \r\n        	 placeholder=&quot;Отзыв о серисе&quot;&gt;&lt;/textarea&gt; \r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n&lt;/table&gt;\r\n\r\n	&lt;/table&gt;\r\n		&lt;/form&gt;\r\n	&lt;/body&gt;\r\n&lt;/html&gt;</code></pre>', '2020-04-26', 'file/v3.jpg'),
(4, 'Вариант 4', '<pre><code class=\"language-html\">&lt;html&gt;\r\n	&lt;head&gt;\r\n		&lt;meta charset=&quot;utf-8&quot;&gt;\r\n		&lt;title&gt;4&lt;/title&gt;\r\n\r\n		&lt;style type=&quot;text/css&quot;&gt;\r\n	   		TABLE {\r\n    			border-collapse: collapse;\r\n\r\n   			}\r\n   			TD, TH {\r\n    			padding: 12px;\r\n    			border: 2px solid black ;\r\n\r\n   			\r\n  		&lt;/style&gt;\r\n		\r\n	&lt;/head&gt;\r\n	&lt;body&gt;\r\n		&lt;h1&gt;Магазин обуви&lt;/h1&gt;\r\n		&lt;form name=&quot;Shop&quot; method=&quot;post&quot;&gt;\r\n	\r\n    &lt;table&gt;\r\n    &lt;tr&gt;\r\n        &lt;td&gt;\r\n            &lt;b&gt;Тип обуви: &lt;/b&gt; &lt;br&gt;\r\n                        &lt;SELECT &gt;\r\n                            &lt;OPTION value=Любая&gt;Любая\r\n                            &lt;OPTION value=&quot;Ботинки&quot;&gt;Ботинки\r\n                            &lt;OPTION value=&quot;Кроссовки&quot;&gt;Кроссовки\r\n                            &lt;OPTION value=&quot;Летняя обувь&quot;&gt;Летняя обувь\r\n                            &lt;OPTION value=&quot;Туфли&quot;&gt;Туфли\r\n                           \r\n                        &lt;/select&gt;\r\n        &lt;/td&gt;\r\n        &lt;td&gt;\r\n            &lt;b&gt;Бренд: &lt;/b&gt; &lt;br&gt;\r\n                        &lt;SELECT &gt;\r\n                            &lt;OPTION value=Любой&gt;Любой\r\n                            &lt;OPTION value=&quot;adidas&quot;&gt;Adidas\r\n                            &lt;OPTION value=&quot;nike&quot;&gt;Nike\r\n                            &lt;OPTION value=&quot;NB&quot;&gt;NB\r\n                            &lt;OPTION value=&quot;Native&quot;&gt;Native\r\n                            &lt;OPTION value=&quot;Premiata&quot;&gt;Premiata\r\n                            &lt;OPTION value=&quot;Dr. Martens&quot;&gt;Dr. Martens\r\n                            &lt;OPTION value=&quot;The North Face&quot;&gt;The North Face   \r\n                           \r\n                        &lt;/select&gt;\r\n\r\n         &lt;/td&gt;\r\n        &lt;td&gt;\r\n            &lt;B&gt;Цена  до (в тыс. руб): &lt;/B&gt; &lt;br&gt;\r\n                &lt;b&gt;0&lt;/b&gt;\r\n                &lt;input type=&quot;range&quot;\r\n                    min=&quot;0&quot;\r\n                    max=&quot;50&quot;\r\n                    step=&quot;1&quot;\r\n                    value=&quot;25&quot;&gt;\r\n            &lt;B&gt;50&lt;/B&gt;\r\n        &lt;/td&gt;\r\n        &lt;td align=&quot;center&quot;&gt;\r\n            &lt;input name=&quot;sezon&quot; type=&quot;radio&quot; value=&quot;Демисезон&quot; checked&gt; Демисезон\r\n                        &lt;input name=&quot;sezon&quot; type=&quot;radio&quot; value=&quot;Лето&quot; &gt; Лето\r\n                        &lt;input name=&quot;sezon&quot; type=&quot;radio&quot; value=&quot;Зима&quot;&gt; Зима\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td&gt;\r\n              &lt;b&gt;Размеры: &lt;/b&gt; &lt;br&gt;&lt;br&gt;\r\n                       EU 36 &lt;input type=checkbox &gt;\r\n                        EU 37 &lt;input type=checkbox &gt;&lt;br&gt;\r\n                        EU 38 &lt;input type=checkbox &gt;\r\n                        EU 39 &lt;input type=checkbox &gt;&lt;br&gt;\r\n                        EU 40 &lt;input type=checkbox &gt;\r\n                        EU 41 &lt;input type=checkbox &gt;&lt;br&gt;\r\n                        EU 42 &lt;input type=checkbox &gt;\r\n                        EU 43 &lt;input type=checkbox &gt;&lt;br&gt;\r\n                         EU 44 &lt;input type=checkbox &gt;\r\n                          EU 45 &lt;input type=checkbox &gt;\r\n        &lt;/td&gt;\r\n        &lt;td&gt;\r\n            &lt;b&gt;Пол: &lt;/b&gt; &lt;br&gt;&lt;br&gt;\r\n                       Мужской &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n                        Женский &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n                        Дети &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n        &lt;/td&gt;\r\n        &lt;td align=&quot;center&quot;&gt;&lt;b&gt;Цвета: &lt;/b&gt;&lt;br&gt;\r\n\r\n            &lt;font color=&quot;#FF0000&quot;&gt;Красный&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;font color=&quot;#4169E1&quot;&gt;Синий&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;font color=&quot;#4B0082&quot;&gt;Индиго&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;font color=&quot;#000000&quot;&gt;Черный&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;font color=&quot;#008000&quot;&gt;Зеленый&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;font color=&quot;#FFA500&quot;&gt;Оранжевый&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;font color=&quot;#A52A2A&quot;&gt;Коричневый&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;font color=&quot;#FFD700&quot;&gt;Желтый&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            \r\n        \r\n\r\n\r\n        &lt;/td&gt;\r\n        &lt;td align=&quot;center&quot;&gt;&lt;b&gt;Загрузите фотографию и мы найдем похожую обувь&lt;/b&gt;&lt;br&gt;&lt;br&gt;\r\n\r\n            &lt;input type=file name=prm10&gt;&lt;/td&gt;\r\n\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td colspan=&quot;2&quot; align=&quot;center&quot;&gt;&lt;button  class=&quot;new&quot;&gt;Найти &lt;/button&gt;&lt;/td&gt;\r\n        &lt;td colspan=&quot;2&quot;&gt;\r\n             &lt;textarea name=prm9 rows=3 cols= 77 placeholder=&quot;Отзыв о серисе&quot;&gt;&lt;/textarea&gt; \r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n&lt;/table&gt;\r\n\r\n\r\n		&lt;/form&gt;\r\n	&lt;/body&gt;\r\n&lt;/html&gt;</code></pre>', '2020-04-26', 'file/v4.jpg'),
(5, 'Вариант 5', '<pre><code class=\"language-html\">&lt;html&gt;\r\n	&lt;head&gt;\r\n		&lt;meta charset=&quot;utf-8&quot;&gt;\r\n		&lt;title&gt;5&lt;/title&gt;\r\n\r\n		&lt;style type=&quot;text/css&quot;&gt;\r\n	   		TABLE {\r\n    			border-collapse: collapse;\r\n\r\n   			}\r\n   			TD, TH {\r\n    			padding: 12px;\r\n    			border: 2px solid black ;\r\n\r\n           \r\n\r\n   			\r\n  		&lt;/style&gt;\r\n		\r\n	&lt;/head&gt;\r\n	&lt;body&gt;\r\n		&lt;h1&gt;Поиск книг&lt;/h1&gt;\r\n		&lt;form name=&quot;Shop&quot; method=&quot;post&quot;&gt;\r\n	\r\n    &lt;table&gt;\r\n    &lt;tr&gt;\r\n        &lt;td rowspan=&quot;2&quot;&gt;\r\n         \r\n    \r\n        &lt;div style=&quot;float: left; width: 140px; height: 220px;&quot; align=&quot;left&quot;&gt; &lt;br&gt;&lt;b&gt;Тип: &lt;/b&gt; &lt;br&gt;\r\n                        \r\n\r\n      &lt;input name=&quot;1&quot; type=&quot;radio&quot; value=&quot;Любой&quot; checked&gt; \r\n      Любой&lt;br&gt;\r\n      &lt;input name=&quot;1&quot; type=&quot;radio&quot; value=&quot;Художественная литература&quot; &gt; \r\n      Художественная литература&lt;br&gt;\r\n      &lt;input name=&quot;1&quot; type=&quot;radio&quot; value=&quot;Комиксы&quot;&gt;\r\n       Комиксы&lt;br&gt;\r\n      &lt;input name=&quot;1&quot; type=&quot;radio&quot; value=&quot;Журналы&quot;&gt; \r\n      Журналы&lt;br&gt;\r\n      &lt;input name=&quot;1&quot; type=&quot;radio&quot; value=&quot;Научная литература&quot;&gt; \r\n      Научная литература&lt;br&gt;\r\n      &lt;input name=&quot;1&quot; type=&quot;radio&quot; value=&quot;Манга&quot;&gt;\r\n       Манга&lt;br&gt;\r\n      &lt;input name=&quot;1&quot; type=&quot;radio&quot; value=&quot;Учебная&quot;&gt; \r\n      Учебная&lt;br&gt;\r\n\r\n\r\n                    &lt;/div&gt;\r\n        &lt;div style=&quot;float: left; width: 140px; height: 220px;&quot; \r\n        align=&quot;center&quot;&gt; &lt;br&gt;&lt;b&gt;Жанр / тема: &lt;/b&gt; &lt;br&gt;\r\n                        &lt;SELECT &gt;\r\n                            &lt;OPTION value=Любой&gt;Любой\r\n                            &lt;OPTION value=&quot;Комедия&quot;&gt;Комедия\r\n                            &lt;OPTION value=&quot;Драма&quot;&gt;Драма\r\n                            &lt;OPTION value=&quot;Ужасы&quot;&gt;Ужасы\r\n                            &lt;OPTION value=&quot;Трагедия&quot;&gt;Трагедия\r\n                            &lt;OPTION value=&quot;Физика&quot;&gt;Физика\r\n                            &lt;OPTION value=&quot;Математика&quot;&gt;Математика\r\n                            &lt;OPTION value=&quot;Мода&quot;&gt;Мода \r\n                            &lt;OPTION value=&quot;Огород&quot;&gt;Огород \r\n                           \r\n                        &lt;/select&gt; \r\n                        &lt;br&gt;&lt;br&gt;&lt;br&gt;\r\n                        &lt;font color=&quot;#008000&quot;&gt;В подарок&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;\r\n                        &lt;font color=&quot;#FF0000&quot;&gt;Специальное издание&lt;/font&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n        &lt;/div&gt;\r\n\r\n        &lt;/td&gt;\r\n        &lt;td colspan=&quot;2&quot; align=&quot;center&quot;&gt;\r\n        &lt;b&gt;Название: &lt;/b&gt;&lt;input type=text  &gt; &lt;b&gt;Автор: &lt;/b&gt;&lt;input type=text  &gt; &lt;br&gt;\r\n        &lt;b&gt;Год: &lt;/b&gt;&lt;input type=date  placeholder=&quot;дд.мм.гггг&quot;&gt; &lt;b&gt; Код ISBN: &lt;/b&gt;&lt;input type=number  &gt; &lt;br&gt;\r\n    &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td&gt;&lt;b&gt;Доставка: &lt;/b&gt;&lt;br&gt;&lt;br&gt;\r\n            &lt;input name=&quot;2&quot; type=&quot;radio&quot; value=&quot;&quot; checked&gt; Без доставки&lt;br&gt;\r\n                        &lt;input name=&quot;2&quot; type=&quot;radio&quot; value=&quot;Москва&quot; &gt;Москва &lt;br&gt;\r\n                        &lt;input name=&quot;2&quot; type=&quot;radio&quot; value=&quot;Московская обл.&quot;&gt; Московская обл.&lt;br&gt;\r\n                        &lt;input name=&quot;2&quot; type=&quot;radio&quot; value=&quot;Санкт-Питербург&quot;&gt; Санкт - Питербург&lt;br&gt;\r\n                        &lt;input name=&quot;2&quot; type=&quot;radio&quot; value=&quot;Россия(почта)&quot;&gt; Россия (почта)&lt;br&gt;\r\n                        \r\n        &lt;/td&gt;\r\n        &lt;td&gt;\r\n             &lt;b&gt;Поиск в библиотеках&lt;/b&gt; &lt;input type=checkbox &gt;&lt;br&gt;&lt;br&gt;\r\n             &lt;b&gt;Поиск в магазинах&lt;/b&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n             &lt;B&gt;Цена до (в тыс. руб): &lt;/B&gt; &lt;br&gt;\r\n                &lt;b&gt;0&lt;/b&gt;\r\n                &lt;input type=&quot;range&quot;\r\n                    min=&quot;0&quot;\r\n                    max=&quot;10&quot;\r\n                    step=&quot;0.1&quot;\r\n                    value=&quot;5&quot;&gt;\r\n            &lt;B&gt;10&lt;/B&gt;\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td&gt;\r\n            &lt;textarea name=prm9 rows=3 cols= 37 placeholder=&quot;Поиск по  тексту&quot;&gt;&lt;/textarea&gt; \r\n        &lt;/td&gt;\r\n        &lt;td colspan=&quot;2&quot; rowspan=&quot;2&quot; align=&quot;center&quot;&gt;\r\n            \r\n\r\n        &lt;div style=&quot;float: left; width: 130px; height: 85px;&quot; align=&quot;right&quot;&gt;\r\n            &lt;b&gt;Alib.ru&lt;/b&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;b&gt;Book24&lt;/b&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;b&gt;Буквоед&lt;/b&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n            &lt;b&gt;Читай–город&lt;/b&gt; &lt;input type=checkbox &gt;&lt;br&gt;\r\n        &lt;/div&gt;\r\n        &lt;div style=&quot;float: left; width: 300px; height: 85px;&quot; align=&quot;right&quot;&gt;\r\n            &lt;b&gt;Библиотека им. Н.А. Некросова&lt;/b&gt; &lt;input type=checkbox &gt;\r\n             &lt;b&gt;Библиотека им. А.П. Гайдара&lt;/b&gt; &lt;input type=checkbox &gt;\r\n             &lt;b&gt;Библиотека им. А.С. Пушкина&lt;/b&gt; &lt;input type=checkbox &gt;\r\n             &lt;b&gt;Библиотека им. А.П. Боголюбова&lt;/b&gt; &lt;input type=checkbox &gt;\r\n        &lt;/div&gt;\r\n        \r\n\r\n\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td align=&quot;center&quot;&gt;&lt;button  class=&quot;new&quot;&gt;Найти &lt;/button&gt;&lt;/td&gt;\r\n    &lt;/tr&gt;\r\n&lt;/table&gt;\r\n\r\n\r\n		&lt;/form&gt;\r\n	&lt;/body&gt;\r\n&lt;/html&gt;</code></pre>', '2020-04-26', 'file/v5.jpg'),
(6, 'Вариант 6', '<pre><code class=\"language-html\">&lt;html&gt;\r\n	&lt;head&gt;\r\n		&lt;meta charset=&quot;utf-8&quot;&gt;\r\n		&lt;title&gt;6&lt;/title&gt;\r\n\r\n		&lt;style type=&quot;text/css&quot;&gt;\r\n	   		TABLE {\r\n    			border-collapse: collapse;\r\n\r\n   			}\r\n   			TD, TH {\r\n    			padding: 12px;\r\n    			border: 2px solid black ;\r\n\r\n           \r\n\r\n   			\r\n  		&lt;/style&gt;\r\n		\r\n	&lt;/head&gt;\r\n	&lt;body&gt;\r\n		&lt;h1&gt;Запись к врачу&lt;/h1&gt;\r\n		&lt;form name=&quot;Shop&quot; method=&quot;post&quot;&gt;\r\n\r\n           &lt;table&gt;\r\n    &lt;tr&gt;\r\n        &lt;td colspan=&quot;2&quot;&gt;&lt;b&gt;ФИО: &lt;/b&gt;&lt;input type=text size=&quot;35&quot;&gt; &lt;br&gt;&lt;br&gt;\r\n        &lt;b&gt;Дата рождения: &lt;/b&gt;&lt;input type=date size=&quot;25&quot;&gt; &lt;br&gt;\r\n    &lt;/td&gt;\r\n        &lt;td colspan=&quot;2&quot;&gt;\r\n        	&lt;b&gt;Адрес больницы: &lt;/b&gt;&lt;input type=text size=&quot;59&quot;&gt; &lt;br&gt;\r\n        &lt;/td&gt;\r\n    &lt;/tr&gt;\r\n    &lt;tr&gt;\r\n        &lt;td colspan=&quot;2&quot;&gt;\r\n        	&lt;b&gt;Номер полиса ОМС: &lt;/b&gt;&lt;/b&gt;&lt;input type=number &gt; &lt;br&gt;\r\n        &lt;/td&gt;\r\n        &lt;td &gt;\r\n        	&lt;b&gt;Специальность: &lt;/b&gt; \r\n                        &lt;SELECT &gt;\r\n              &lt;OPTION value=Акушер&gt;Акушер\r\n              &lt;OPTION value=Аллерголог&gt;Аллерголог\r\n              &lt;OPTION value=Дерматолог&gt;Дерматолог\r\n              &lt;OPTION value=Нарколог&gt;Нарколог\r\n              &lt;OPTION value=Невролог&gt;Невролог\r\n              &lt;OPTION value=Окулист&gt;Окулист\r\n              &lt;OPTION value=Стоматолог&gt;Стоматолог\r\n              &lt;OPTION value=Терапевт&gt;Терапевт\r\n              &lt;OPTION value=Хирург&gt;Хирург\r\n                           \r\n                        &lt;/select&gt; \r\n         	    &lt;br&gt;&lt;br&gt;\r\n         	   \r\n         	    &lt;div styl